import React from "react";
import { Route, Switch } from "react-router-dom";

import Signup from "./Signup";
import Signin from "./Signin";
import Header from "./Header";
import Home from "./Home";
import Showquiz from "./Showquiz";
import Quizset from "./Quizset";
import Createquiz from "./Createquiz";
import Showmarks from "./Showmarks";
import Profile from "./Profile";
import Notfound from "./Notfound";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      islogged: false
    };
  }

  handleIslogged = value => {
    this.setState({ islogged: value });
  };

  PublicRoutes() {
    return (
      <>
        <Switch>
          <Route exact path="/">
            <Header handleIslogged={this.handleIslogged} /> <Home />
          </Route>

          <Route exact path="/signup">
            <Header handleIslogged={this.handleIslogged} /> <Signup />
          </Route>
          <Route exact path="/signin">
            <Header handleIslogged={this.handleIslogged} />
            <Signin handleIslogged={this.handleIslogged} />
          </Route>
          <Route exact path="/admin/login">
            <Header handleIslogged={this.handleIslogged} />
            <Signin handleIslogged={this.handleIslogged} />
          </Route>
          <Route exact path="/*">
            <Notfound />
          </Route>
        </Switch>
      </>
    );
  }
  PrivateRoutes() {
    return (
      <>
        <Switch>
          <Route exact path="/">
            <Header handleIslogged={this.handleIslogged} /> <Home />
          </Route>
          <Route exact path="/quizsets">
            <Header handleIslogged={this.handleIslogged} /> <Quizset />
          </Route>
          <Route exact path="/myprofile">
            <Header handleIslogged={this.handleIslogged} /> <Profile />
          </Route>
          <Route exact path="/createquiz">
            <Header handleIslogged={this.handleIslogged} /> <Createquiz />
          </Route>
          <Route exact path="/quizsets/:quizname">
            <Header handleIslogged={this.handleIslogged} /> <Showquiz />
          </Route>
          <Route exact path="/users/:username/marks">
            <Header handleIslogged={this.handleIslogged} /> <Showmarks />
          </Route>
          <Route exact path="/*">
            <Notfound />
          </Route>
        </Switch>
      </>
    );
  }

  render() {
    return (
      <div className="whole-app">
        {localStorage.quizuserToken || localStorage.quizAdminToken
          ? this.PrivateRoutes()
          : this.PublicRoutes()}
      </div>
    );
  }
}
export default App;
