import React from "react";
import { Link } from "react-router-dom";

import "../styles/Signup.css";

class UserSignin extends React.Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: ""
    };
  }

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };
  handleSignin = event => {
    console.log(event);
    event.preventDefault();
    fetch("http://localhost:3000/api/v1/users/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        email: this.state.email,
        password: this.state.password
      })
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        if (data.email) {
          alert(data.message);
        } else if (!data.success) {
          alert(data.message);
        } else if (data.success) {
          alert("successfully loggedin");
          localStorage.setItem("quizuserToken", data.token);
          localStorage.setItem("quizuserName", data.userName);
          this.props.handleIslogged(true);
          this.props.history.push("/quizlist");
        }
      });
  };

  render() {
    return (
      <center className="signin-bg">
        <div className="signup-div">
          <div className="right-section">
            <h1 className="rightside-header">Hello, Friend!</h1>
            <p className="rightside-para">
              You can start a amazing journey and have fun with this
            </p>
            <Link className="signup-button" to="/signup">
              SIGN UP
            </Link>
          </div>
          <div className="left-section">
            <h1 className="leftside-header">Sign In to journey</h1>

            <div className="input-icons">
              <i className="fas fa-envelope icon"></i>
              <input
                className="input-email"
                type="email"
                name="email"
                value={this.state.email}
                onChange={this.handleChange}
                placeholder="Email"
              />
            </div>
            <div className="input-icons">
              <i className="fas fa-lock icon"></i>
              <input
                className="input-password"
                type="password"
                name="password"
                value={this.state.password}
                onChange={this.handleChange}
                placeholder="Password"
              />
            </div>
            <button onClick={this.handleSignin} className="signup-button">
              SIGN IN
            </button>
          </div>
        </div>
      </center>
    );
  }
}

export default UserSignin;
