import React from "react";
import "../styles/Showquiz.css";

class Showmarks extends React.Component {
  constructor() {
    super();
    this.state = {
      totalmarks: ""
    };
  }
  componentDidMount() {
    fetch("http://localhost:3000/api/v1/user", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: localStorage.quizuserToken
      }
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        if (data.success) {
          this.setState({ totalmarks: data.user.marksId.reverse() });
        }
        console.log(this.state.totalmarks);
      });
  }
  render() {
    let { totalmarks } = this.state;
    return (
      <>
        <div className="showmarks-section">
          {totalmarks.length ? (
            <div>
              <p>All MArks</p>
              {totalmarks.map((marks, index) => (
                <div className="marks">
                  <p>{index + 1}.</p>
                  <h3 className="quizsetname-marks">{marks.quizname},</h3>
                  <h3 className="quiz-marks">
                    {marks.mark}
                    <span className="marks-span">marks</span>
                  </h3>
                </div>
              ))}
            </div>
          ) : (
            <p>No Marks Appeared</p>
          )}
        </div>
      </>
    );
  }
}
export default Showmarks;
