import React from "react";
import { Link } from "react-router-dom";

import "../styles/Signup.css";

class Signup extends React.Component {
  constructor() {
    super();
    this.state = {
      username: "",
      email: "",
      password: ""
    };
  }

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleUserSignUp = event => {
    console.log(event);
    event.preventDefault();
    fetch("http://localhost:3000/api/v1/users", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username: this.state.username,
        email: this.state.email,
        password: this.state.password
      })
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        if (data.user) {
          this.props.history.push("/signin");
        }
      });
  };

  handleAdminSignup = e => {
    e.preventDefault();
    fetch("http://localhost:3001/api/v1/admins", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        adminname: this.state.adminname,
        adminemail: this.state.adminemail,
        adminpassword: this.state.adminpassword
      })
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          console.log(data);
          alert("successfully registered");
          this.props.history.push("/admin/login");
        } else {
          alert("enter valid details");
        }
      });
  };

  render() {
    return (
      <center>
        <div className="signup-div">
          <div className="left-section">
            <h1 className="leftside-header"> Sign Up for User </h1>
            <form>
              <div className="input-icons">
                <i className="fas fa-user icon"></i>
                <input
                  className="input-username"
                  type="text"
                  name="username"
                  onChange={this.handleChange}
                  value={this.state.username}
                  placeholder="UserName"
                />
              </div>
              <div className="input-icons">
                <i className="fas fa-envelope icon"></i>

                <input
                  className="input-email"
                  type="email"
                  name="email"
                  value={this.state.email}
                  onChange={this.handleChange}
                  placeholder="Email"
                />
              </div>
              <div className="input-icons">
                <i className="fas fa-lock icon"></i>
                <input
                  className="input-password"
                  type="password"
                  name="password"
                  value={this.state.password}
                  onChange={this.handleChange}
                  placeholder="Password"
                />
              </div>
              <button onClick={this.handleUserSignUp} className="signup-button">
                SIGN UP
              </button>
            </form>
          </div>
          <div className="right-section">
            <h1 className="rightside-header">Sign Up for Admin</h1>

            <div className="input-icons">
              <i className="fas fa-user icon"></i>
              <input
                className="input-username"
                type="text"
                name="username"
                onChange={this.handleChange}
                value={this.state.adminname}
                placeholder="Name"
              />
            </div>
            <div className="input-icons">
              <i className="fas fa-envelope icon"></i>
              <input
                className="input-email"
                type="email"
                name="email"
                value={this.state.adminemail}
                onChange={this.handleChange}
                placeholder="Email"
              />
            </div>
            <div className="input-icons">
              <i className="fas fa-lock icon"></i>
              <input
                className="input-password"
                type="password"
                name="password"
                value={this.state.adminpassword}
                onChange={this.handleChange}
                placeholder="Password"
              />
            </div>
            <button onClick={this.handleAdminSignup} className="signup-button">
              SIGN UP
            </button>
          </div>
          {/* <div className="right-section">
            <h1 className="rightside-header">Hello, Friend!</h1>
            <p className="rightside-para">
              You can start a amazing journey and have fun with this
            </p>

            <Link className="signup-button" to="/signin">
              SIGN IN
            </Link>
          </div> */}
        </div>
      </center>
    );
  }
}

export default Signup;
