import React from "react";
import { Link } from "react-router-dom";
import { withRouter } from "react-router-dom";

import "../styles/Signup.css";

class Signin extends React.Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      adminemail: "",
      admimpassword: ""
    };
  }

  handleadminChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleuserChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleUserSignin = event => {
    console.log(event);
    event.preventDefault();
    fetch("http://localhost:3000/api/v1/users/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        email: this.state.email,
        password: this.state.password
      })
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        if (data.email) {
          alert(data.message);
        } else if (!data.user) {
          alert(data.message);
        } else if (data.user) {
          alert("successfully loggedin");
          localStorage.setItem("quizuserToken", data.token);
          localStorage.setItem("quizuserName", data.userName);
          this.props.handleIslogged(true);
          this.props.history.push("/quizlist");
        }
      });
  };

  handleAdminSignin = e => {
    e.preventDefault();
    fetch("http://localhost:3000/api/v1/admins/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        adminEmail: this.state.adminEmail,
        adminPassword: this.state.adminPassword
      })
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        if (data.email) {
          alert(data.message);
        } else if (!data.success) {
          alert(data.message);
        } else if (data.success) {
          alert("successfully loggedin");
          localStorage.setItem("quizAdminToken", data.token);
          localStorage.setItem("quizAdminName", data.adminName);
          this.props.handleIslogged(true);
          this.props.history.push("/");
        }
      });
  };

  render() {
    return (
      <center className="signin-bg">
        <div className="signup-div">
          <div className="left-section">
            <h1 className="leftside-header">Sign In for Admin</h1>

            <div className="input-icons">
              <i className="fas fa-envelope icon"></i>
              <input
                className="input-email"
                type="email"
                name="email"
                value={this.state.adminemail}
                onChange={this.handleadminChange}
                placeholder="Email"
              />
            </div>
            <div className="input-icons">
              <i className="fas fa-lock icon"></i>
              <input
                className="input-password"
                type="password"
                value={this.state.adminpassword}
                onChange={this.handleadminChange}
                placeholder="Password"
              />
            </div>
            <button onClick={this.handleAdminSignin} className="signup-button">
              SIGN IN
            </button>
          </div>
          <div className="right-section">
            <h1 className="rightside-header">Sign In for User</h1>

            <div className="input-icons">
              <i className="fas fa-envelope icon"></i>
              <input
                className="input-email"
                type="email"
                name="email"
                value={this.state.email}
                onChange={this.handleuserChange}
                placeholder="Email"
              />
            </div>
            <div className="input-icons">
              <i className="fas fa-lock icon"></i>
              <input
                className="input-password"
                type="password"
                name="password"
                value={this.state.password}
                onChange={this.handleuserChange}
                placeholder="Password"
              />
            </div>
            <button onClick={this.handleUserSignin} className="signup-button">
              SIGN IN
            </button>
          </div>
        </div>
      </center>
    );
  }
}

export default withRouter(Signin);
