import React from "react";
import { Link } from "react-router-dom";
import { withRouter } from "react-router-dom";

import "../styles/Signup.css";

class Signin extends React.Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      adminemail: "",
      adminpassword: ""
    };
  }

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleUserSignin = event => {
    event.preventDefault();
    fetch("http://localhost:3000/api/v1/users/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        email: this.state.email,
        password: this.state.password
      })
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        if (data.token) {
          alert("successfully loggedin");
          localStorage.setItem("quizuserToken", data.token);
          localStorage.setItem("quizusername", data.username);
          this.props.handleIslogged(true);
          console.log("qwerrt");
          this.props.history.push("/quizsets");
        }
      });
  };

  handleAdminSignin = event => {
    event.preventDefault();
    fetch("http://localhost:3000/api/v1/admins/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        adminemail: this.state.adminemail,
        adminpassword: this.state.adminpassword
      })
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        if (data.success) {
          alert("successfully loggedin");
          localStorage.setItem("quizAdminToken", data.token);
          this.props.handleIslogged(true);
          this.props.history.push("/createquiz");
        }
      });
  };

  render() {
    return (
      <center className="signin-bg">
        <div className="signup-div">
          <div className="left-section">
            <h1 className="leftside-header">Sign In for Admin</h1>

            <div className="input-icons">
              <i className="fas fa-envelope icon"></i>
              <input
                className="input-email"
                type="email"
                name="adminemail"
                value={this.state.adminemail}
                onChange={this.handleChange}
                placeholder="Email"
              />
            </div>
            <div className="input-icons">
              <i className="fas fa-lock icon"></i>
              <input
                className="input-password"
                type="password"
                name="adminpassword"
                value={this.state.adminpassword}
                onChange={this.handleChange}
                placeholder="Password"
              />
            </div>
            <button onClick={this.handleAdminSignin} className="signup-button">
              SIGN IN
            </button>
          </div>
          <div className="right-section">
            <h1 className="rightside-header">Sign In for User</h1>

            <div className="input-icons">
              <i className="fas fa-envelope icon"></i>
              <input
                className="input-email"
                type="email"
                name="email"
                value={this.state.email}
                onChange={this.handleChange}
                placeholder="Email"
              />
            </div>
            <div className="input-icons">
              <i className="fas fa-lock icon"></i>
              <input
                className="input-password"
                type="password"
                name="password"
                value={this.state.password}
                onChange={this.handleChange}
                placeholder="Password"
              />
            </div>
            <button onClick={this.handleUserSignin} className="signup-button">
              SIGN IN
            </button>
            <p className="signup-first">
              Don't have an account
              <Link to="/signup" className="span">
                Signup
              </Link>
              First
            </p>
          </div>
        </div>
      </center>
    );
  }
}

export default withRouter(Signin);
